query albums_tracks_genre_some {
  allAlbums(condition: {artistId: 127}) {
    nodes {
      albumId
      title
      tracksByAlbumId {
        nodes {
          trackId
          name
          genreByGenreId {
            name
          }
        }
      }
      artistByArtistId {
        albumsByArtistId {
          nodes {
            tracksByAlbumId {
              nodes {
                mediaTypeByMediaTypeId {
                  name
                }
                genreByGenreId {
                  name
                }
              }
            }
          }
        }
      }
    }
  }
}
